import React, { useState, useEffect, useRef } from "react";
import styles from "./Dropdown.module.css";

function Dropdown() {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState(`지역을 선택해 주세요`);
  const dropdownRef = useRef(null); // Ref 객체를 생성하여 드롭다운 컨테이너를 참조

  const region = ["전체", "서울", "경기", "부산", "대구", "인천"]; // 드롭다운 박스의 옵션들을 배열로 정의

  // 드롭다운 컨테이너 외부를 클릭하면 드롭다운 내용을 닫을 수 있게 하는 기능
  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };
    document.addEventListener("click", handleOutsideClick);
    return () => {
      document.removeEventListener("click", handleOutsideClick);
    };
  }, []);

  // 드롭다운 버튼을 클릭했을 때 isOpen 상태를 토글하여 드롭다운 내용을 보이거나 숨김
  const handleDropdownClick = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className={styles.dropdown_container}>
      <button
        className={`${styles.drop_btn} ${isOpen ? styles.opened : ""}`}
        onClick={handleDropdownClick}
      >
        {selectedOption}
      </button>
      {isOpen && (
        <div className={styles.dropdown_content}>
          {region.map((option) => (
            <div
              key={option}
              className={styles.option}
              onClick={(event) => {
                event.stopPropagation();
                setSelectedOption(option);
              }}
            >
              {option}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Dropdown;
