import styles from "./SearchResults.module.css";
import React, { useState } from "react";

function truncateText(text, maxLength) {
  if (text.length > maxLength) {
    return text.slice(0, maxLength) + "..";
  }
  return text;
}

function SearchResults() {
  const [cards, setCards] = useState([]);
  // 아마도.. 데이터 받아와서 cards 배열에 넣고 map으로 card 띄우는 고런 느낌으로 구현하면 될 듯..?

  // 임시로 배열 & 검색결과로 만들어 봄
  const card = [
    "청년전용창업자금(창업기반지원자금)",
    "청년내일저축계좌",
    "청년 자기개발 도서구입비 지원",
    "예비창업패키지",
    "청년키움식당",
    "(창업성공패키지 지원사업)청년창업사관학교",
    "아유.. 하기 싫어..",
    "이제는 더 이상 물러날 곳이 없다  ",
    "창틀에 앉아 내성적이고 말 잘 안하고...내가 걷고 있고 옆에 여자들이 많은데, 나는 몰라",
    "예... 하고는 있는데",
    "전문가한테 맡기면 편할 텐데...",
    "자.. 이게 클릭이야..",
  ];
  const [search, setSearch] = useState("창업");

  return (
    <div className={styles.SearchResults}>
      <div className={styles.search_text}>
        <h1>
          <span>'{search}'</span> 검색 결과
        </h1>
      </div>
      <div className={styles.card_box}>
        {card.map((a) => (
          <div key={a} className={styles.card}>
            <div className={styles.card_text}>{truncateText(a, 42)}</div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SearchResults;
