import styles from "./Onboarding.module.css";
import logo from "../img/logo.png";
import axios from "axios";
import Dropdown from "../component/Dropdown";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Onboarding() {
  let navigate = useNavigate();

  // 로고에 적용할 클릭 이벤트 함수
  const goToWelcome = () => {
    navigate("/");
  };

  const residenceOptions = {
    전체: "ALL",
    서울: "SEOUL",
    부산: "BUSAN",
    대구: "DAEGU",
    인천: "INCHEON",
    광주: "GWANGJU",
    대전: "DAEJEON",
    울산: "ULSAN",
    경기: "GYOUNGGI",
    강원: "GANGWON",
    충북: "CHUNGBUK",
    충남: "CHUNGNAM",
    전북: "JEONBUK",
    전남: "JEONNAM",
    경북: "GYEONGBUK",
    경남: "GYEONGNAM",
    제주: "JEJU",
    세종: "SEJONG",
  };
  const educationOptions = {
    "고졸 미만": "UNDER_HIGH_SCHOOL",
    "고교 졸업": "HIGH_SCHOOL_GRADUATE",
    "대학 재학": "COLLEGE_STUDENT",
    "대학 졸업": "COLLEGE_GRADUATE",
    "석∙박사": "DOCTORATE",
    제한없음: "ALL",
  };
  const jobStatusOptions = {
    재직자: "EMPLOYEE",
    자영업자: "SELF_EMPLOYED",
    미취업자: "JOB_SEEKER",
    프리랜서: "FREELANCER",
    "(예비)창업자": "ENTERPRENEUR",
    단기근로자: "TEMPORARY_WORKER",
    제한없음: "ALL",
  };

  const [age, setAge] = useState(null);

  // region, education, jobStatus 드롭다운 컴포넌트에서 선택된 옵션을 상태로 관리
  const [selectedResidence, setSelectedResidence] = useState(null);
  const [selectedEducation, setSelectedEducation] = useState(null);
  const [selectedJobStatus, setSelectedJobStatus] = useState(null);

  // "선택완료" 버튼이 활성화되는지 여부를 결정하는 변수
  const isButtonDisabled =
    !selectedResidence || !selectedEducation || !selectedJobStatus || !age;

  //  Onboarding 컴포넌트에서 선택한 정보를 userData 상태로 저장
  const [userData, setUserData] = useState({
    residence: "",
    education: "",
    jobStatus: "",
    age: null,
  });

  // Onboarding 컴포넌트 내부에 prepareFiltersForServer 함수 정의 추가
  const prepareFiltersForServer = (userInfo) => {
    // 연령은 정수형으로 그대로 사용
    const age = userInfo?.age;

    // 선택한 학력, 구직 상태, 지역을 서버에 맞게 변환
    const residence = residenceOptions[userInfo?.residence] || null;
    const education = educationOptions[userInfo?.education] || null;
    const jobStatus = jobStatusOptions[userInfo?.jobStatus] || null;

    return {
      age,
      residence,
      education,
      jobStatus,
    };
  };

  // Onboarding 컴포넌트 내부에 setRecommendationData 함수 정의 추가
  const setRecommendationData = (data) => {
    // 맞춤 일자리 정책 추천 데이터를 상태로 업데이트
    setRecommendationData(data);
  };

  // 버튼 클릭 : localStorage에 사용자 정보 저장 후 '홈' 페이지로 이동
  const goToHome = () => {
    // age 값을 userData에 저장
    const updatedUserData = {
      ...userData,
      age: age,
    };

    // 사용자 정보를 서버에 전송
    const filtersForServer = prepareFiltersForServer(updatedUserData);

    // API 요청을 위한 쿼리스트링 생성
    const apiUrl = `http://52.79.114.100/api/policies?residence=${filtersForServer.residenceOptions}&education=${filtersForServer.educationOptions}&jobStatus=${filtersForServer.jobStatusOptions}&pageSize=4&age=${age}`;

    axios
      .get(apiUrl)
      .then((response) => {
        // API에서 받아온 맞춤 일자리 정책 추천 데이터를 상태로 업데이트
        setRecommendationData(response.data.recommendations);
      })
      .catch((error) => {
        console.error("Error fetching recommendation data from API:", error);
      });

    // navigate 이동
    navigate("/Home");
  };

  return (
    <div className={styles.Onboarding}>
      <div className={styles.logo}>
        <img src={logo} alt="logo" />
      </div>
      <div className={styles.wrapper}>
        <div className={styles.Onboarding_container}>
          <div className={styles.Onboarding_content}>
            <div className={styles.Onboarding_line}>
              <span className={styles.bold}>지역</span>
              <Dropdown
                options={residenceOptions}
                className={styles.Onboarding_dropdown}
                defaultOption="지역을 선택해주세요"
                onSelect={(option) => {
                  setSelectedResidence(option);
                  setUserData((prevUserData) => ({
                    ...prevUserData,
                    residence: option,
                  }));
                }} // 옵션 선택 시 선택한 옵션을 상태로 업데이트
              />
            </div>
            <div className={styles.Onboarding_line}>
              <span className={styles.bold}>학력</span>
              <Dropdown
                options={educationOptions}
                className={styles.Onboarding_dropdown}
                defaultOption="학력을 선택해주세요"
                onSelect={(option) => {
                  setSelectedEducation(option);
                  setUserData((prevUserData) => ({
                    ...prevUserData,
                    education: option,
                  }));
                }}
              />
            </div>
            <div className={styles.Onboarding_line}>
              <span className={styles.bold}>구직 상태</span>
              <Dropdown
                options={jobStatusOptions}
                className={styles.Onboarding_dropdown}
                defaultOption="구직 상태를 선택해주세요"
                onSelect={(option) => {
                  setSelectedJobStatus(option);
                  setUserData((prevUserData) => ({
                    ...prevUserData,
                    jobStatus: option,
                  }));
                }}
              />
            </div>
            <div
              className={`${styles.Onboarding_line} ${styles.age_container}`}
            >
              <span className={styles.bold}>연령</span>
              <div className={styles.age_text}>
                <span>만</span>
                <input
                  value={age === null ? "" : age} // null일 경우 빈 문자열로 표시
                  onChange={(e) => {
                    const value = e.target.value; // 입력 값
                    if (/^\d*$/.test(value)) {
                      // 정규식으로 숫자인지 검사
                      setAge(value);
                    }
                  }}
                />
              </div>
              <span className={styles.agefinish}>세</span>
            </div>
          </div>
        </div>
        <div className={styles.wrapper_bottom}>
          <button
            className={`${styles.Onboarding_btn} ${
              isButtonDisabled ? styles.disabled : ""
            }`}
            disabled={isButtonDisabled}
            onClick={() => {
              goToHome();
            }}
          >
            선택 완료
          </button>
        </div>
      </div>
    </div>
  );
}

export default Onboarding;
