import styles from "./Pagination.module.css";

const Pagination = () => {
  return (
    <div className={styles.pagination}>
      <button
        className={styles.prev_btn}
        onClick={() => setActivePage(Math.max(activePage - 5, 1))}
        disabled={activePage === 1}
      >
        &lt;
      </button>

      {Array.from(
        { length: Math.min(visiblePageRange, totalPages) },
        (_, index) => {
          const pageNumber = Math.floor((activePage - 1) / 5) * 5 + index + 1;
          return (
            <button
              key={pageNumber}
              onClick={() => {
                if (pageNumber <= totalPages) {
                  setActivePage(pageNumber);
                }
              }}
              className={
                activePage === pageNumber
                  ? `${styles.pageButton} ${styles.active}`
                  : styles.pageButton
              }
            >
              {pageNumber}
            </button>
          );
        }
      )}

      <button
        className={styles.next_btn}
        onClick={() => setActivePage(Math.min(activePage + 5, totalPages))}
        disabled={activePage + 5 > totalPages}
      >
        &gt;
      </button>
    </div>
  );
};

export default Pagination;
