import Modal from "react-modal";
import Dropdown from "./Dropdown";
import React, { useState, useRef } from "react";
import filter from "../img/filter.png";
import cancel from "../img/cancel.png";
import styles from "./ModalContent.module.css";

const ModalContent = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const handleModalOpen = () => {
    setIsSelectionComplete(false); // 모달 열 때 선택 완료 상태 초기화
    setModalIsOpen(true);
  };

  const regionOptions = [
    "전체",
    "서울",
    "부산",
    "대구",
    "인천",
    "광주",
    "대전",
    "울산",
    "경기",
    "강원",
    "충북",
    "충남",
    "전북",
    "전남",
    "경북",
    "경남",
    "제주",
    "세종",
  ];
  const educationOptions = [
    "고졸 미만",
    "고교 재학",
    "고졸 예정",
    "고교 졸업",
    "대학 재학",
    "대졸 예정",
    "대학 졸업",
    "석∙박사",
    "제한없음",
  ];
  const jobStatusOptions = [
    "재직자",
    "자영업자",
    "미취업자",
    "프리랜서",
    "일용근로자",
    "(예비)창업자",
    "단기근로자",
    "영농종사자",
    "제한없음",
  ];

  const [age, setAge] = useState(null);

  // region, education, jobStatus 드롭다운 컴포넌트에서 선택된 옵션을 상태로 관리ㄴ
  const [selectedRegion, setSelectedRegion] = useState(null);
  const [selectedEducation, setSelectedEducation] = useState(null);
  const [selectedJobStatus, setSelectedJobStatus] = useState(null);

  // "선택완료" 버튼이 활성화되는지 여부를 결정하는 변수
  const isButtonDisabled =
    !selectedRegion || !selectedEducation || !selectedJobStatus || !age;

  return (
    <div>
      <button className={styles.filterbutton} onClick={handleModalOpen}>
        <img src={filter} className={styles.filtericon} alt="filtericon"></img>
      </button>
      <Modal
        className={styles.modal}
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
      >
        <div className={styles.modalbody}>
          <div className={styles.header}>
            <div className={styles.headerleft}>
              <img src={filter} className={styles.headericon}></img>
              <span>필터</span>
            </div>
            <div className={styles.headerright}>
              <button
                className={styles.filterx}
                onClick={() => setModalIsOpen(false)}
              >
                <img src={cancel} alt="cancel"></img>
              </button>
            </div>
          </div>
          <div className={styles.content}>
            <div className={styles.Onboarding_content}>
              <div className={styles.Onboarding_line}>
                <h4>지역</h4>
                <Dropdown
                  options={regionOptions}
                  defaultOption="지역을 선택해주세요"
                  onSelect={(option) => setSelectedRegion(option)} // 옵션 선택 시 선택한 옵션을 상태로 업데이트
                />
              </div>
              <div className={styles.Onboarding_line}>
                <h4>학력</h4>
                <Dropdown
                  options={educationOptions}
                  defaultOption="학력을 선택해주세요"
                  onSelect={(option) => setSelectedEducation(option)}
                />
              </div>
              <div className={styles.Onboarding_line}>
                <h4>구직 상태</h4>
                <Dropdown
                  options={jobStatusOptions}
                  defaultOption="구직 상태를 선택해주세요"
                  onSelect={(option) => setSelectedJobStatus(option)}
                />
              </div>
              <div
                className={`${styles.Onboarding_line} ${styles.age_container}`}
              >
                <h4>연령</h4>
                <div>
                  <input
                    value={age}
                    onChange={(e) => {
                      setAge(e.target.value);
                    }}
                    placeholder="만"
                  />
                  <span>세</span>
                </div>
              </div>
            </div>
          </div>
          <button
            className={`${styles.filterfinish} ${
              areAllFieldsSelected ? "" : styles.disabled
            }`}
            onClick={() => setModalIsOpen(false)}
            disabled={!areAllFieldsSelected}
          >
            선택 완료
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default ModalContent;
