import React from "react";
import { useNavigate, useParams } from "react-router-dom";
import logo from "../img/logo.png";
import styles from "./DetailPage.module.css";
import Footer from "../component/Footer";
import axios from "axios";

const DetailPage = () => {
  const navigate = useNavigate();

  const { bizid } = useParams();
  const [detailInfo, setDetailInfo] = useState(null);

  useEffect(() => {
    const apiUrl = `http://52.79.114.100/api/policies/${bizid}`;

    axios
      .get(apiUrl)
      .then((response) => {
        const {
          name,
          bizid,
          detail,
          field,
          content,
          managePeriod,
          applicationPeriod,
          scale,
          ageInfoMin,
          ageInfoMax,
          education,
          major,
          jobStatus,
          specialization,
          additionalCondition,
          restrictedPaticipant,
        } = response.data;

        setDetailInfo({
          name,
          bizid,
          detail,
          field,
          content,
          managePeriod,
          applicationPeriod,
          scale,
          ageInfoMin,
          ageInfoMax,
          education,
          major,
          jobStatus,
          specialization,
          additionalCondition,
          restrictedPaticipant,
        });
      })
      .catch((error) => {
        console.error("Error fetching detail data:", error);
      });
  }, [bizid]);

  if (!detailInfo) {
    return <div>Loading...</div>;
  }

  const goToHome = () => {
    navigate("/Home");
  };

  return (
    <div>
      <div className={styles.logo} onClick={goToHome}>
        <img src={logo} alt="logo" />
      </div>
      <div className={styles.header}>
        <span className={styles.title}>{detailInfo.name}</span>
        <span className={styles.intro}>{detailInfo.detail}</span>
      </div>
      <div className={styles.content}>
        <div className={styles.detail}>
          <span className={styles.detailtitle}>정책 상세 설명</span>
          <ul className={styles.detailcontent}>
            <li>
              <span>정책 번호 : {detailInfo.bizid}</span>
            </li>
          </ul>
        </div>
        <div className={styles.detail}>
          <span className={styles.detailtitle}>신청자격</span>
          <ul className={styles.detailcontent}>내용</ul>
        </div>
        <button>신청하기</button>
      </div>
      <Footer />
    </div>
  );
};

export default DetailPage;
