import logo from "../img/logo.png";
import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "./Home.module.css";
import ModalContent from "../component/ModalContent";
import AutoComplete from "../component/AutoComplete";
import Footer from "../component/Footer";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [options, setOptions] = useState([]);
  const [rankData, setRankData] = useState([]);
  const [recommendationData, setRecommendationData] = useState([]); // 맞춤 일자리 정책 추천 데이터

  const navigate = useNavigate();

  const residenceOptions = {
    전체: "ALL",
    서울: "SEOUL",
    부산: "BUSAN",
    대구: "DAEGU",
    인천: "INCHEON",
    광주: "GWANGJU",
    대전: "DAEJEON",
    울산: "ULSAN",
    경기: "GYOUNGGI",
    강원: "GANGWON",
    충북: "CHUNGBUK",
    충남: "CHUNGNAM",
    전북: "JEONBUK",
    전남: "JEONNAM",
    경북: "GYEONGBUK",
    경남: "GYEONGNAM",
    제주: "JEJU",
    세종: "SEJONG",
  };
  const educationOptions = {
    "고졸 미만": "UNDER_HIGH_SCHOOL",
    "고교 졸업": "HIGH_SCHOOL_GRADUATE",
    "대학 재학": "COLLEGE_STUDENT",
    "대학 졸업": "COLLEGE_GRADUATE",
    "석∙박사": "DOCTORATE",
    제한없음: "ALL",
  };
  const jobStatusOptions = {
    재직자: "EMPLOYEE",
    자영업자: "SELF_EMPLOYED",
    미취업자: "JOB_SEEKER",
    프리랜서: "FREELANCER",
    "(예비)창업자": "ENTERPRENEUR",
    단기근로자: "TEMPORARY_WORKER",
    제한없음: "ALL",
  };

  const [searchZIndex, setSearchZIndex] = useState(1); // 모달창을 열고 닫을때 검색창의 z-index를 조절(안하면 모달창 열어도 검색창 뜸)

  // 클라이언트에서 사용자가 선택한 값들을 서버에 전달하기 위한 함수
  const prepareFiltersForServer = (userInfo) => {
    // 연령은 정수형으로 그대로 사용
    const age = userInfo?.age;

    // 선택한 학력, 구직 상태, 지역을 서버에 맞게 변환
    const residence = residenceOptions[userInfo?.residence] || null;
    const education = educationOptions[userInfo?.education] || null;
    const jobStatus = jobStatusOptions[userInfo?.jobStatus] || null;

    return {
      age,
      residence,
      education,
      jobStatus,
    };
  };

  // 자동 완성을 위한 API
  useEffect(() => {
    const apiUrl = "http://52.79.114.100/api/policies?pageSize=10";
    const params = {
      age: null,
      education: null,
      jobStatus: null,
      residence: null,
    };

    axios
      .get(apiUrl, { params })
      .then((response) => {
        const newOptions = response.data.content.map((item) => item.name);
        setOptions(newOptions);
      })
      .catch((error) => {
        console.error("Error fetching data from API:", error);
      });
  }, []);

  // 실시간 정책 순위를 위한 API
  useEffect(() => {
    // API 요청
    const apiUrl = "http://52.79.114.100/api/policies/rank?pageSize=5";

    axios
      .get(apiUrl)
      .then((response) => {
        // API에서 받아온 데이터 중에서 이름(name) 정보와 bizid를 추출하여 rankData에 설정
        const newRankData = response.data.map((item) => ({
          name: item.name,
          bizId: item.bizId,
        }));
        setRankData(newRankData);
      })
      .catch((error) => {
        console.error("Error fetching rank data:", error);
      });
  }, []); // 빈 배열을 넣어 한 번만 호출되도록 설정

  // 정책 상세 페이지로 이동
  const goToDetailPage = (bizId) => {
    // 해당 정책의 bizid를 사용하여 DetailPage로 이동
    navigate(`/DetailPage/${bizId}`);
  };

  // 카드에 표시할 정책 API
  useEffect(() => {
    // 로컬 스토리지에서 사용자 정보 가져오기
    const userInfo = JSON.parse(localStorage.getItem("userInfo"));

    // 사용자가 선택한 필터 정보를 서버 형식으로 변환
    const filtersForServer = prepareFiltersForServer(userInfo);

    // API 요청을 위한 쿼리스트링 생성
    const apiUrl = `http://52.79.114.100/api/policies?region=${filtersForServer.residence}&education=${filtersForServer.education}&jobStatus=${filtersForServer.jobStatus}&pageSize=4&age=${filtersForServer.age}`;

    axios
      .get(apiUrl)
      .then((response) => {
        // API에서 받아온 데이터 처리
        const newOptions = response.data.content.map((item) => item.name);
        setOptions(newOptions);
        // 필요한 경우, 맞춤 일자리 정책 추천 데이터를 상태로 업데이트
        // 예: setRecommendationData(response.data.recommendations);
      })
      .catch((error) => {
        console.error("Error fetching data from API:", error);
      });
  }, []); // 빈 배열을 넣어 한 번만 호출되도록 설정

  const handleCardClick = (bizId) => {
    goToDetailPage(bizId);
  };

  // 모달 열릴 때 z-index 값을 변경
  const openModal = () => {
    setSearchZIndex(0);
  };

  // 모달 닫힐 때 z-index 값을 원래대로 변경
  const closeModal = () => {
    setSearchZIndex(1);
  };

  const goToHome = () => {
    navigate("/Home");
  };

  // 24자 초과할 경우 ..으로 표시
  function cutText(text, maxLength) {
    if (text.length > maxLength) {
      return text.slice(0, maxLength) + "..";
    }
    return text;
  }

  return (
    <div className={styles.Home}>
      <div className={styles.logo} onClick={goToHome}>
        <img src={logo} alt="logo" />
      </div>
      <div className={styles.search} style={{ zIndex: searchZIndex }}>
        <AutoComplete options={options}></AutoComplete>
      </div>
      <div className={styles.recommend}>
        <div className={styles.recotitle}>
          <span>맞춤 일자리 정책 추천</span>
          <ModalContent openModal={openModal} closeModal={closeModal} />
        </div>
        <div className={styles.card_box}>
          {recommendationData.map((policy, index) => (
            <div
              key={index}
              className={styles.card}
              onClick={() => handleCardClick(policy.bizId)}
            >
              <div className={styles.card_text}>{cutText(policy.name, 24)}</div>
            </div>
          ))}
        </div>
      </div>
      <div className={styles.rank}>
        <div className={styles.ranktitle}>
          <span>실시간 청년 일자리 정책 순위</span>
        </div>
        <div className={styles.ranklist}>
          <ul>
            {/* rankData를 이용하여 정책 순위 리스트 생성 */}
            {rankData.map((policy, index) => (
              <li key={index}>
                <span className={styles.ranknum}>{index + 1}</span>
                {/* 정책 이름 버튼을 클릭할 때 해당 정책의 bizid를 전달 */}
                <button
                  className={styles.jobtitle}
                  onClick={() => goToDetailPage(policy.bizId)}
                >
                  {policy.name}
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
