import React, { useState } from "react";
import styles from "./Dropdown.module.css";

function Dropdown() {
  const [isOpen, setIsOpen] = useState(false);

  const [selectedOption, setSelectedOption] = useState("지역을 선택해 주세요");

  const options = ["전체", "서울", "경기", "부산", "대구", "인천"]; // 드롭다운 박스의 옵션들을 배열로 정의

  // 드롭다운 버튼을 클릭했을 때 호출되는 함수
  const handleDropdownClick = (e) => {
    e.stopPropagation(); // 이벤트 버블링 방지(깜빡이고 바로 사라지는 오류 방지)
    setIsOpen(!isOpen); // isOpen 상태를 토글하여 드롭다운 내용을 보이거나 숨김
  };

  // 드롭다운 내용의 각 옵션을 클릭했을 때 호출되는 함수
  const handleOptionClick = (option) => {
    setSelectedOption(option); // 선택된 옵션을 selectedOption 상태로 설정
    setIsOpen(false); // 드롭다운 내용을 숨김
  };

  return (
    <div className={styles.dropdown_container}>
      {/* 드롭다운 버튼 */}
      <button className={styles.drop_btn} onClick={handleDropdownClick}>
        {/* 선택된 옵션 표시 */}
        {selectedOption}
      </button>
      {/* isOpen이 true일 때만 드롭다운 내용 보여줌 */}
      {isOpen && (
        <div className={styles.dropdown_content}>
          {/* 옵션들 매핑 */}
          {options.map((option, index) => (
            // 각 옵션을 클릭했을 때 handleOptionClick 함수 호출
            <div
              key={index}
              className={styles.option}
              onClick={() => handleOptionClick(option)}
            >
              {option}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Dropdown;
