import Modal from "react-modal";
import Dropdown from "./Dropdown";
import React, { useState, useEffect } from "react";
import axios from "axios";
import filter from "../img/filter.png";
import cancel from "../img/cancel.png";
import styles from "./ModalContent.module.css";
import { useNavigate } from "react-router-dom";

const ModalContent = ({ openModal, closeModal }) => {
  let navigate = useNavigate();

  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [filteredPolicyName, setFilteredPolicyName] = useState(null);

  const handleModalOpen = () => {
    setModalIsOpen(true);
  };

  const regionOptions = [
    "전체",
    "서울",
    "부산",
    "대구",
    "인천",
    "광주",
    "대전",
    "울산",
    "경기",
    "강원",
    "충북",
    "충남",
    "전북",
    "전남",
    "경북",
    "경남",
    "제주",
    "세종",
  ];
  const educationOptions = [
    "고졸 미만",
    "고교 재학",
    "고졸 예정",
    "고교 졸업",
    "대학 재학",
    "대졸 예정",
    "대학 졸업",
    "석∙박사",
    "제한없음",
  ];
  const jobStatusOptions = [
    "재직자",
    "자영업자",
    "미취업자",
    "프리랜서",
    "일용근로자",
    "(예비)창업자",
    "단기근로자",
    "영농종사자",
    "제한없음",
  ];

  const [age, setAge] = useState(null);
  const [selectedRegion, setSelectedRegion] = useState(regionOptions[0]);
  const [selectedEducation, setSelectedEducation] = useState(
    educationOptions[0]
  );
  const [selectedJobStatus, setSelectedJobStatus] = useState(
    jobStatusOptions[0]
  );

  // Modal이 열릴 때마다 저장된 사용자 정보를 가져와서 미리 설정
  useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem("userInfo"));
    if (userInfo) {
      setSelectedRegion(userInfo.region || regionOptions[0]);
      setSelectedEducation(userInfo.education || educationOptions[0]);
      setSelectedJobStatus(userInfo.jobStatus || jobStatusOptions[0]);
      setAge(userInfo.age);
    }
  }, []);

  // 로컬 스토리지에서 저장된 필터링 옵션을 불러옴
  const storedFilterOptions =
    JSON.parse(localStorage.getItem("filterOptions")) || {};

  // Modal이 열릴 때마다 저장된 사용자 정보와 필터링 옵션을 가져와서 미리 설정
  useEffect(() => {
    const userInfo = JSON.parse(localStorage.getItem("userInfo"));
    if (userInfo) {
      setSelectedRegion(userInfo.region || regionOptions[0]);
      setSelectedEducation(userInfo.education || educationOptions[0]);
      setSelectedJobStatus(userInfo.jobStatus || jobStatusOptions[0]);
      setAge(userInfo.age);
    }

    // 로컬 스토리지에 저장된 필터링 옵션을 모달 내부의 상태로 설정
    setSelectedRegion(storedFilterOptions.region || regionOptions[0]);
    setSelectedEducation(storedFilterOptions.education || educationOptions[0]);
    setSelectedJobStatus(storedFilterOptions.jobStatus || jobStatusOptions[0]);
    setAge(storedFilterOptions.age);
  }, []);

  //  Onboarding 컴포넌트에서 선택한 정보를 userData 상태로 저장(userData의 초기 값을 localStorage 정보로 교체!)
  const [userData, setUserData] = useState({
    region: selectedRegion,
    education: selectedEducation,
    jobStatus: selectedJobStatus,
    age: age,
  });

  // 필터링 값이 변경될 때마다 userData 업데이트
  useEffect(() => {
    setUserData({
      region: selectedRegion,
      education: selectedEducation,
      jobStatus: selectedJobStatus,
      age: age,
    });

    // 로컬 스토리지에도 userData 저장
    localStorage.setItem("userInfo", JSON.stringify(userData));
  }, [selectedRegion, selectedEducation, selectedJobStatus, age]);

  // 버튼 클릭 : localStorage에 사용자 정보 저장 후 '홈' 페이지로 이동
  const goToHome = () => {
    // age 값을 userData에 저장
    const updatedUserData = {
      ...userData,
      age: age,
    };

    // userData를 업데이트한 후 localStorage에 저장
    localStorage.setItem("userInfo", JSON.stringify(updatedUserData));

    // navigate 이동
    navigate("/Home");
  };

  // 필터링 버튼 클릭 시 필터링 결과 처리 함수 호출
  const handleFilterResults = async () => {
    try {
      // API 엔드포인트 주소 생성
      const apiUrl = `http://52.79.114.100/api/policies?region=${selectedRegion}&education=${selectedEducation}&jobStatus=${selectedJobStatus}&age=${age}`;

      // 서버에 필터링 정보를 보내서 정책 이름을 가져옴
      const policyName = await fetchFilteredPolicyNameFromServer({
        region: selectedRegion,
        education: selectedEducation,
        jobStatus: selectedJobStatus,
        age: age,
      });

      if (policyName) {
        // 서버에서 정책 이름을 정상적으로 가져왔을 경우, 카드에 보여줌
        console.log("맞춤 필터링 결과에 맞는 정책의 이름:", policyName);
        setFilteredPolicyName(policyName); // 카드에 보여줄 필터링된 정책 이름 설정
      } else {
        console.log(
          "맞춤 필터링 결과에 맞는 정책의 이름을 가져오지 못했습니다."
        );
        // 오류 처리 또는 사용자에게 알리는 로직을 추가
      }

      localStorage.setItem("filterOptions", JSON.stringify(filteredUserData));

      // 모달을 닫음
      setModalIsOpen(false);
      closeModal();
    } catch (error) {
      console.error("Error handling filter results:", error);
      // 오류 처리 또는 사용자에게 알리는 로직을 추가
    }
  };

  return (
    <div>
      <button
        className={styles.filterbutton}
        onClick={() => {
          handleModalOpen();
          openModal();
        }}
      >
        <img src={filter} className={styles.filtericon} alt="filtericon"></img>
      </button>
      <Modal
        className={styles.modal}
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
      >
        <div className={styles.modalbody}>
          <div className={styles.header}>
            <div className={styles.headerleft}>
              <img src={filter} className={styles.headericon}></img>
              <span>필터</span>
            </div>
            <div className={styles.headerright}>
              <button
                className={styles.filterx}
                onClick={() => {
                  setModalIsOpen(false);
                  closeModal();
                }}
              >
                <img src={cancel} alt="cancel"></img>
              </button>
            </div>
          </div>
          <div className={styles.content}>
            <div className={styles.Onboarding_content}>
              <div className={styles.Onboarding_line}>
                <span className={styles.bold}>지역</span>
                <Dropdown
                  options={regionOptions}
                  className={styles.Onboarding_dropdown}
                  defaultOption="지역을 선택해주세요"
                  onSelect={(option) => {
                    setSelectedRegion(option);
                    setUserData((prevUserData) => ({
                      ...prevUserData,
                      region: option,
                    }));
                  }} // 옵션 선택 시 선택한 옵션을 상태로 업데이트
                />
              </div>
              <div className={styles.Onboarding_line}>
                <span className={styles.bold}>학력</span>
                <Dropdown
                  options={educationOptions}
                  className={styles.Onboarding_dropdown}
                  defaultOption="학력을 선택해주세요"
                  onSelect={(option) => {
                    setSelectedEducation(option);
                    setUserData((prevUserData) => ({
                      ...prevUserData,
                      education: option,
                    }));
                  }}
                />
              </div>
              <div className={styles.Onboarding_line}>
                <span className={styles.bold}>구직 상태</span>
                <Dropdown
                  options={jobStatusOptions}
                  className={styles.Onboarding_dropdown}
                  defaultOption="구직 상태를 선택해주세요"
                  onSelect={(option) => {
                    setSelectedJobStatus(option);
                    setUserData((prevUserData) => ({
                      ...prevUserData,
                      jobStatus: option,
                    }));
                  }}
                />
              </div>
              <div
                className={`${styles.Onboarding_line} ${styles.age_container}`}
              >
                <span className={styles.bold}>연령</span>
                <div className={styles.age_text}>
                  <span>만</span>
                  <input
                    value={age === null ? "" : age} // null일 경우 빈 문자열로 표시
                    onChange={(e) => {
                      const value = e.target.value; // 입력 값
                      if (/^\d*$/.test(value)) {
                        // 정규식으로 숫자인지 검사
                        setAge(value);
                      }
                    }}
                  />
                </div>
                <span className={styles.agefinish}>세</span>
              </div>
            </div>
          </div>
          <div className={styles.wrapper_bottom}>
            {/* 어차피 필터는 온보딩 등록된 후 -> 나이를 지우는 경우만 생각해서 비활성화 기능 만들었음 */}
            <button
              className={`${styles.Onboarding_btn} ${
                !age ? styles.disabled : ""
              }`}
              disabled={!age}
              onClick={handleFilterResults}
            >
              선택 완료
            </button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default ModalContent;
