import styles from "./Onboarding.module.css";
import logo from "../img/logo.png";
import Dropdown from "../component/Dropdown";
import ModalContent from "../component/ModalContent";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Onboarding() {
  let navigate = useNavigate();

  // 로고에 적용할 클릭 이벤트 함수
  const goToWelcome = () => {
    navigate("/");
  };

  const regionOptions = [
    "전체",
    "서울",
    "부산",
    "대구",
    "인천",
    "광주",
    "대전",
    "울산",
    "경기",
    "강원",
    "충북",
    "충남",
    "전북",
    "전남",
    "경북",
    "경남",
    "제주",
    "세종",
  ];
  const educationOptions = [
    "고졸 미만",
    "고교 재학",
    "고졸 예정",
    "고교 졸업",
    "대학 재학",
    "대졸 예정",
    "대학 졸업",
    "석∙박사",
    "제한없음",
  ];
  const jobStatusOptions = [
    "재직자",
    "자영업자",
    "미취업자",
    "프리랜서",
    "일용근로자",
    "(예비)창업자",
    "단기근로자",
    "영농종사자",
    "제한없음",
  ];

  // 초기값을 로컬 스토리지에서 가져오는 함수
  const getInitialValues = () => {
    const storedData = JSON.parse(localStorage.getItem("userInfo"));
    return storedData || {}; // 로컬 스토리지에 저장된 값이 없으면 빈 객체 반환
  };

  const [selectedRegion, setSelectedRegion] = useState("");
  const [selectedEducation, setSelectedEducation] = useState("");
  const [selectedJobStatus, setSelectedJobStatus] = useState("");
  const [age, setAge] = useState(null);

  // "선택완료" 버튼이 활성화되는지 여부를 결정하는 변수
  const isButtonDisabled =
    !selectedRegion || !selectedEducation || !selectedJobStatus || !age;

  //  Onboarding 컴포넌트에서 선택한 정보를 userData 상태로 저장
  const [userData, setUserData] = useState({
    region: "",
    education: "",
    jobStatus: "",
    age: null,
  });

  // 버튼 클릭 : localStorage에 사용자 정보 저장 후 '홈' 페이지로 이동
  const goToHome = () => {
    // age 값을 userData에 저장
    const updatedUserData = {
      ...userData,
      age: age,
    };

    // userData를 업데이트한 후 localStorage에 저장
    localStorage.setItem("userInfo", JSON.stringify(updatedUserData));

    // navigate 이동
    navigate("/Home");
  };

  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleModalContentClose = (selectedData) => {
    localStorage.setItem("userInfo", JSON.stringify(selectedData));
    navigate("/Home");
  };

  // 선택한 정보를 로컬 스토리지에 저장
  useEffect(() => {
    const selectedData = {
      region: selectedRegion,
      education: selectedEducation,
      jobStatus: selectedJobStatus,
      age: age,
    };
    localStorage.setItem("selectedData", JSON.stringify(selectedData));
  }, [selectedRegion, selectedEducation, selectedJobStatus, age]);

  return (
    <div className={styles.Onboarding}>
      <div className={styles.logo}>
        <img src={logo} alt="logo" />
      </div>
      <div className={styles.wrapper}>
        <div className={styles.Onboarding_container}>
          <div className={styles.Onboarding_content}>
            <div className={styles.Onboarding_line}>
              <span className={styles.bold}>지역</span>
              <Dropdown
                options={regionOptions}
                className={styles.Onboarding_dropdown}
                defaultOption="지역을 선택해주세요"
                onSelect={(option) => {
                  setSelectedRegion(option);
                  setUserData((prevUserData) => ({
                    ...prevUserData,
                    region: option,
                  }));
                }} // 옵션 선택 시 선택한 옵션을 상태로 업데이트
              />
            </div>
            <div className={styles.Onboarding_line}>
              <span className={styles.bold}>학력</span>
              <Dropdown
                options={educationOptions}
                className={styles.Onboarding_dropdown}
                defaultOption="학력을 선택해주세요"
                onSelect={(option) => {
                  setSelectedEducation(option);
                  setUserData((prevUserData) => ({
                    ...prevUserData,
                    education: option,
                  }));
                }}
              />
            </div>
            <div className={styles.Onboarding_line}>
              <span className={styles.bold}>구직 상태</span>
              <Dropdown
                options={jobStatusOptions}
                className={styles.Onboarding_dropdown}
                defaultOption="구직 상태를 선택해주세요"
                onSelect={(option) => {
                  setSelectedJobStatus(option);
                  setUserData((prevUserData) => ({
                    ...prevUserData,
                    jobStatus: option,
                  }));
                }}
              />
            </div>
            <div
              className={`${styles.Onboarding_line} ${styles.age_container}`}
            >
              <span className={styles.bold}>연령</span>
              <div className={styles.age_text}>
                <span>만</span>
                <input
                  value={age === null ? "" : age} // null일 경우 빈 문자열로 표시
                  onChange={(e) => {
                    const value = e.target.value; // 입력 값
                    if (/^\d*$/.test(value)) {
                      // 정규식으로 숫자인지 검사
                      setAge(value);
                    }
                  }}
                />
              </div>
              <span className={styles.agefinish}>세</span>
            </div>
          </div>
        </div>
        <div className={styles.wrapper_bottom}>
          <button
            className={`${styles.Onboarding_btn} ${
              isButtonDisabled ? styles.disabled : ""
            }`}
            disabled={isButtonDisabled}
            onClick={() => {
              setIsModalOpen(true);
              goToHome();
            }}
          >
            선택 완료
          </button>
        </div>
        {isModalOpen && (
          <ModalContent
            isOpen={isModalOpen}
            onRequestClose={() => setIsModalOpen(false)}
            initialSelectedRegion={selectedRegion}
            initialSelectedEducation={selectedEducation}
            initialSelectedJobStatus={selectedJobStatus}
            initialAge={age}
            onModalClose={handleModalContentClose}
          />
        )}
      </div>
    </div>
  );
}

export default Onboarding;
