import React, { useState } from "react";
import search from "../img/search.png";
import styles from "./AutoComplete.module.css";
import { useNavigate } from "react-router-dom";

const AutoComplete = ({ options }) => {
  let navigate = useNavigate();

  const [inputValue, setInputValue] = useState("");
  const [filteredOptions, setFilteredOptions] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);

  const handleChange = (e) => {
    const value = e.target.value;
    setInputValue(value);

    const filtered = options.filter((option) =>
      // 수정된 부분: 문자열이 어느 위치에 있던지 검사하여 보여줍니다.
      option.toLowerCase().includes(value.toLowerCase())
    );

    setFilteredOptions(filtered);
    // 수정된 부분: 입력값이 있는 경우에만 드롭다운을 보여줍니다.
    setShowDropdown(value.length > 0 && filtered.length > 0);
  };

  const handleSelectOption = (option) => {
    setInputValue(option);
    setFilteredOptions([]);
    setShowDropdown(false);
  };

  const handleBlur = () => {
    setShowDropdown(false);
  };

  // 버튼에 적용할 클릭 이벤트 함수
  const goToSearchResults = () => {
    if (inputValue.length > 0) {
      navigate("/SearchResults");
    }
  };

  // 검색창에 검색한 텍스트를 로컬 스토리지에 저장 : 홈 페이지에서 저장하고 검색 결과 페이지에서 불러올 수 있음
  const handleSearch = () => {
    localStorage.setItem("searchText", inputValue);
  };

  return (
    <div className={styles.dropdown}>
      <div className={styles.searchbox}>
        <input
          type="text"
          value={inputValue}
          onChange={handleChange}
          onBlur={handleBlur}
          onFocus={() => setShowDropdown(true)}
          placeholder="궁금한 일자리 정책을 찾아보세요!"
        />
        <button
          className={styles.search_btn}
          onClick={() => {
            goToSearchResults();
            handleSearch();
          }}
        >
          <img src={search} className={styles.searchicon} alt="searchicon" />
        </button>
      </div>
      {showDropdown && inputValue && (
        <ul className={styles.dropdownbox}>
          {filteredOptions.map((option) => {
            return (
              <li key={option} onClick={() => handleSelectOption(option)}>
                {option}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default AutoComplete;
