import logo from "../img/logo.png";
import React, { useState, useEffect } from "react";
import styles from "./Home.module.css";
import ModalContent from "../component/ModalContent";
import AutoComplete from "../component/AutoComplete";
import Footer from "../component/Footer";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Home = () => {
  const navigate = useNavigate();

  const [searchZIndex, setSearchZIndex] = useState(1); // 모달창을 열고 닫을때 검색창의 z-index를 조절(안하면 모달창 열어도 검색창 뜸)

  // 모달 열릴 때 z-index 값을 변경
  const openModal = () => {
    setSearchZIndex(0);
  };

  // 모달 닫힐 때 z-index 값을 원래대로 변경
  const closeModal = () => {
    setSearchZIndex(1);
  };

  const goToHome = () => {
    navigate("/Home");
  };

  const goToDetailPage = () => {
    navigate("/DetailPage");
  };

  // 24자 초과할 경우 ..으로 표시
  function cutText(text, maxLength) {
    if (text.length > maxLength) {
      return text.slice(0, maxLength) + "..";
    }
    return text;
  }

  // 맞춤 추천 기능 시작

  // 한국어로 저장된 localStorage의 값을 가져와서 영어로 변환하여 Swagger의 파라미터와 일치시켜야 함
  // 변환 매핑 생성
  const translationMap = {
    전체: "ALL",
    부산: "BUSAN",
    충북: "CHUNGBUK",
    충남: "DAEGU",
    대구: "DAEJEON",
    강원: "GANGWON",
    광주: "GWANGJU",
    경북: "GYONGBUK",
    경남: "GYEONGNAM",
    경기: "GYOUNGGI",
    인천: "INCHEON",
    제주: "JEJU",
    전북: "JEONBUK",
    전남: "JEONNAM",
    세종: "SEJONG",
    울산: "ULSAN",
    서울: "SEOUL",
    "고등학교 졸업 미만": "UNDER_HIGH_SCHOOL",
    "고등학교 졸업": "HIGH_SCHOOL_GRADUATE",
    "대학교 재학": "COLLEGE_STUDENT",
    "대학교 졸업": "COLLEGE_GRADUATE",
    "석사/박사": "DOCTORATE",
    무관: "ALL",
    "취업 준비생": "JOB_SEEKER",
    "(예비)창업자": "ENTREPRENEUR",
    재직자: "EMPLOYEE",
    자영업자: "SELF_EMPLOYED",
    프리랜서: "FREELANCER",
    "단기 근로자": "TEMPORARY_WORKER",
  };

  // 로컬 스토리지에서 사용자 정보를 가져옴
  const userInfo = JSON.parse(localStorage.getItem("userInfo"));

  const [translatedData, setTranslatedData] = useState({
    region: "",
    education: "",
    jobStatus: "",
    age: 0,
  });

  // 로컬 스토리지 변경 감시를 위한 상태
  const [localStorageUpdated, setLocalStorageUpdated] = useState(false);

  // 로컬 스토리지 값 변경 시 호출되는 함수
  const handleLocalStorageChange = () => {
    setLocalStorageUpdated(true);
  };

  useEffect(() => {
    // 로컬 스토리지 값 변경 이벤트 등록
    window.addEventListener("storage", handleLocalStorageChange);

    if (localStorageUpdated && userInfo) {
      // 선택된 값들을 기반으로 변환된 값을 생성
      const translatedRegion =
        translationMap[userInfo.region] || userInfo.region;
      const translatedEducation =
        translationMap[userInfo.education] || userInfo.education;
      const translatedJobStatus =
        translationMap[userInfo.jobStatus] || userInfo.jobStatus;
      const userAge = parseInt(userInfo.age);

      setTranslatedData({
        region: translatedRegion,
        education: translatedEducation,
        jobStatus: translatedJobStatus,
        age: userAge,
      });

      // 로컬 스토리지 변경 상태 초기화
      setLocalStorageUpdated(false);
    }

    // 컴포넌트가 언마운트될 때 로컬 스토리지 값 변경 이벤트 제거
    return () => {
      window.removeEventListener("storage", handleLocalStorageChange);
    };
  }, [userInfo, localStorageUpdated]);

  // API 요청 URL 생성(맞춤 추천)
  const constructApiUrl = () => {
    return `http://52.79.114.100/api/policies?age=${translatedData.age}&education=${translatedData.education}&jobStatus=${translatedData.jobStatus}&pageNumber=0&pageSize=4&residence=${translatedData.region}`;
  };

  const [policyData, setPolicyData] = useState({
    content: [], // 초기값은 빈 배열로 설정
  });

  // API 호출 함수 정의
  const fetchPolicies = async () => {
    try {
      const apiUrl = constructApiUrl();
      const response = await axios.get(apiUrl); // API 요청

      const receivedPolicyData = response.data; // 응답 데이터 처리
      console.log(receivedPolicyData); // API 응답 데이터 내용 콘솔창에서 확인
    } catch (error) {
      console.error("Error fetching policies:", error);
    }
  };

  // translatedData가 변경될 때에만 API를 호출
  useEffect(() => {
    if (userInfo) {
      fetchPolicies(); // API 호출
    }
  }, [translatedData]);

  // 맞춤 추천 기능 종료

  return (
    <div className={styles.Home}>
      <div className={styles.logo} onClick={goToHome}>
        <img src={logo} alt="logo" />
      </div>
      <div className={styles.search} style={{ zIndex: searchZIndex }}>
        <AutoComplete />
      </div>
      <div className={styles.recommend}>
        <div className={styles.recotitle}>
          <span>맞춤 일자리 정책 추천</span>
          <ModalContent openModal={openModal} closeModal={closeModal} />
        </div>
        {/* 배열 매핑으로 맞춤 추천 최종 구현!*/}
        <div className={styles.card_box}>
          {policyData.content.map((policy, index) => (
            <div className={styles.card} onClick={goToDetailPage} key={index}>
              <div className={styles.card_text}>{cutText(policy.name, 24)}</div>
            </div>
          ))}
        </div>
      </div>
      <div className={styles.rank}>
        <div className={styles.ranktitle}>
          <span>실시간 청년 일자리 정책 순위</span>
        </div>
        <div className={styles.ranklist}>
          <ul>
            <li onClick={goToDetailPage}>
              <span className={styles.ranknum}>1</span>
              <button className={styles.jobtitle}>정책 이름</button>
            </li>
            <li onClick={goToDetailPage}>
              <span className={styles.ranknum}>2</span>
              <button className={styles.jobtitle}>정책 이름</button>
            </li>
            <li onClick={goToDetailPage}>
              <span className={styles.ranknum}>3</span>
              <button className={styles.jobtitle}>정책 이름</button>
            </li>
            <li onClick={goToDetailPage}>
              <span className={styles.ranknum}>4</span>
              <button className={styles.jobtitle}>정책 이름</button>
            </li>
            <li onClick={goToDetailPage}>
              <span className={styles.ranknum}>5</span>
              <button className={styles.jobtitle}>정책 이름</button>
            </li>
          </ul>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
