import React, { useState, useRef } from "react";
import search from "../img/search.png";
import styles from "./AutoComplete.module.css";
import { useNavigate } from "react-router-dom";

const AutoComplete = ({ options }) => {
  let navigate = useNavigate();
  const inputRef = useRef(null);

  const [inputValue, setInputValue] = useState("");
  const [filteredOptions, setFilteredOptions] = useState([]);

  const handleChange = (e) => {
    const value = e.target.value;
    setInputValue(value);

    const filtered = options.filter((option) =>
      option.toLowerCase().includes(value.toLowerCase())
    );

    setFilteredOptions(filtered);
  };

  const handleSelectOption = (option) => {
    setInputValue(option);
    setFilteredOptions([]);
    inputRef.current.focus(); // 수정된 부분: input에 포커스를 다시 설정하여 클릭 시 입력 포커스가 유지되도록 함
  };

  const handleBlur = () => {
    setTimeout(() => {
      setFilteredOptions([]);
    }, 200); // 수정된 부분: 드롭다운 사라지는 딜레이 추가
  };

  const goToSearchResults = () => {
    if (inputValue.length > 0) {
      navigate("/SearchResults");
    }
  };

  const handleSearch = () => {
    localStorage.setItem("searchText", inputValue);
  };

  return (
    <div className={styles.dropdown}>
      <div className={styles.searchbox}>
        <input
          type="text"
          ref={inputRef}
          value={inputValue}
          onChange={handleChange}
          onBlur={handleBlur}
          onFocus={() => setFilteredOptions(filteredOptions.length > 0)} // 수정된 부분: 드롭다운 내용이 있을 때에만 표시
          placeholder="궁금한 일자리 정책을 찾아보세요!"
        />
        <button
          className={styles.search_btn}
          onClick={() => {
            goToSearchResults();
            handleSearch();
          }}
        >
          <img src={search} className={styles.searchicon} alt="searchicon" />
        </button>
      </div>
      {filteredOptions.length > 0 && (
        <ul className={styles.dropdownbox}>
          {filteredOptions.map((option) => (
            <li
              key={option}
              onClick={() => handleSelectOption(option)}
              className={styles.dropdowntext}
            >
              {option}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default AutoComplete;
