import logo from "../img/logo.png";
import React, { useState, useEffect } from "react";
import axios from "axios";
import styles from "./Home.module.css";
import ModalContent from "../component/ModalContent";
import AutoComplete from "../component/AutoComplete";
import Footer from "../component/Footer";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const [options, setOptions] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [filteredPolicyNames, setFilteredPolicyNames] = useState([]);

  const navigate = useNavigate();

  const [searchZIndex, setSearchZIndex] = useState(1); // 모달창을 열고 닫을때 검색창의 z-index를 조절(안하면 모달창 열어도 검색창 뜸)

  // 모달 열기
  const handleModalOpen = () => {
    setIsModalOpen(true);
    setSearchZIndex(0);
  };

  useEffect(() => {
    const apiUrl = "http://52.79.114.100/api/policies?pageSize=10";
    const params = {
      age: null,
      education: null,
      jobStatus: null,
      residence: null,
    };

    axios
      .get(apiUrl, { params })
      .then((response) => {
        const newOptions = response.data.content.map((item) => item.name);
        setOptions(newOptions);
      })
      .catch((error) => {
        console.error("Error fetching data from API:", error);
      });
  }, []);

  const handleCardClick = (bizid) => {
    navigate(`/DetailPage/${bizid}`);
  };

  // 모달 열릴 때 z-index 값을 변경
  const openModal = () => {
    setSearchZIndex(0);
  };

  // 모달 닫힐 때 z-index 값을 원래대로 변경
  const closeModal = () => {
    setSearchZIndex(1);
  };

  const goToHome = () => {
    navigate("/Home");
  };

  const goToDetailPage = () => {
    navigate("/DetailPage");
  };

  // 24자 초과할 경우 ..으로 표시
  function cutText(text, maxLength) {
    if (text.length > maxLength) {
      return text.slice(0, maxLength) + "..";
    }
    return text;
  }

  // 카드에 필터링된 데이터 표시
  const renderFilteredPolicyNames = (filteredNames) => {
    return filteredNames.map((name, index) => (
      <div key={index} className={styles.filteredCard}>
        {name}
      </div>
    ));
  };

  // ModalContent에서 필터링된 데이터를 받아와 카드에 표시하는 함수
  const handleFilterApplied = (filteredPolicyNames) => {
    // 여기에서 필터링된 데이터를 사용하여 카드를 표시하거나 필요한 처리를 수행합니다.
    console.log("Filtered Policy Names:", filteredPolicyNames);
    // 이 예제에서는 카드에 필터링된 데이터를 표시하는 데서만 로그를 출력합니다.
  };

  return (
    <div className={styles.Home}>
      <div className={styles.logo} onClick={goToHome}>
        <img src={logo} alt="logo" />
      </div>
      <div className={styles.search} style={{ zIndex: searchZIndex }}>
        <AutoComplete options={options}></AutoComplete>
      </div>
      <div className={styles.recommend}>
        <div className={styles.recotitle}>
          <span>맞춤 일자리 정책 추천</span>
          {/* 모달 버튼 클릭 시 모달 열기 */}
          <button
            onClick={() => {
              handleModalOpen();
              openModal();
            }}
          >
            모달 열기
          </button>
          {/* 모달이 열려있을 때만 ModalContent를 렌더링 */}
          {isModalOpen && (
            <ModalContent
              closeModal={closeModal}
              onFilterApplied={handleFilterApplied}
            />
          )}
        </div>
        {/* 필터링된 데이터를 카드에 표시 */}
        {renderFilteredPolicyNames(filteredPolicyNames)}
        <div className={styles.card_box}>
          <div className={styles.card} onClick={goToDetailPage}>
            <div
              onClick={() => handleCardClick(1)}
              className={styles.card_text}
            >
              {cutText("개강이 한달도 안 남았다니 믿기지가 않는다 진심", 24)}
            </div>
          </div>
          <div className={styles.card} onClick={goToDetailPage}>
            <div
              onClick={() => handleCardClick(2)}
              className={styles.card_text}
            >
              {cutText("청년전용창업자금(창업기반지원자금)", 24)}
            </div>
          </div>
          <div className={styles.card} onClick={goToDetailPage}>
            <div
              onClick={() => handleCardClick(3)}
              className={styles.card_text}
            >
              {cutText("청년전용창업자금(창업기반지원자금)", 24)}
            </div>
          </div>
          <div className={styles.card} onClick={goToDetailPage}>
            <div
              onClick={() => handleCardClick(4)}
              className={styles.card_text}
            >
              {cutText("청년전용창업자금(창업기반지원자금)", 24)}
            </div>
          </div>
        </div>
      </div>
      <div className={styles.rank}>
        <div className={styles.ranktitle}>
          <span>실시간 청년 일자리 정책 순위</span>
        </div>
        <div className={styles.ranklist}>
          <ul>
            <li onClick={goToDetailPage}>
              <span className={styles.ranknum}>1</span>
              <button className={styles.jobtitle}>정책 이름</button>
            </li>
            <li onClick={goToDetailPage}>
              <span className={styles.ranknum}>2</span>
              <button className={styles.jobtitle}>정책 이름</button>
            </li>
            <li onClick={goToDetailPage}>
              <span className={styles.ranknum}>3</span>
              <button className={styles.jobtitle}>정책 이름</button>
            </li>
            <li onClick={goToDetailPage}>
              <span className={styles.ranknum}>4</span>
              <button className={styles.jobtitle}>정책 이름</button>
            </li>
            <li onClick={goToDetailPage}>
              <span className={styles.ranknum}>5</span>
              <button className={styles.jobtitle}>정책 이름</button>
            </li>
          </ul>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
