import logo from "../img/logo.png";
import React, { useState, useEffect } from "react";
import styles from "./SearchResults.module.css";
import AutoComplete from "../component/AutoComplete";
import Footer from "../component/Footer";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Pagination from "react-js-pagination";

// 24자 초과할 경우 ..으로 표시
function cutText(text, maxLength) {
  if (text.length > maxLength) {
    return text.slice(0, maxLength) + "..";
  }
  return text;
}

function SearchResults() {
  const [currentPage, setCurrentPage] = useState(1);
  const [totalItems, setTotalItems] = useState(0);
  const itemsPerPage = 12;

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const navigate = useNavigate();

  const goToHome = () => {
    navigate("/Home");
  };

  const goToDetailPage = () => {
    navigate("/DetailPage");
  };

  const [policyData, setPolicyData] = useState({ content: [] }); //  API 응답 데이터를 관리하는 상태 추가

  // 로컬 스토리지에 있던 검색 결과 꺼내오기(문자열로 저장해서 JSON 파싱 안해도 됨)
  const searchText = localStorage.getItem("searchText");

  // 검색 결과를 위한 API 요청 URL 생성
  const constructApiUrl = () => {
    return `http://52.79.114.100/api/policies?keyword=${searchText}&pageNumber=0&pageSize=12`;
  };

  // API 호출 함수 정의
  const fetchPolicies = async () => {
    try {
      const apiUrl = constructApiUrl();
      const response = await axios.get(apiUrl); // API 요청

      setPolicyData(response.data); // policyData 업데이트
      console.log("searchText:", searchText);
      console.log(constructApiUrl());
    } catch (error) {
      console.error("Error fetching policies:", error);
    }
  };

  // searchText가 변경될 때에만 API를 호출
  useEffect(() => {
    if (searchText) {
      fetchPolicies(); // API 호출
    }
  }, [searchText]);

  return (
    <div className={styles.Home}>
      <div className={styles.logo} onClick={goToHome}>
        <img src={logo} alt="logo" />
      </div>
      <div className={styles.search}>
        <AutoComplete />
      </div>
      <div className={styles.SearchResults}>
        <div className={styles.search_text}>
          <h1>
            <span>'{searchText}'</span> 검색 결과
          </h1>
        </div>
        <div className={styles.card_box}>
          {policyData.content.map((policy, index) => (
            <div key={index} className={styles.card} onClick={goToDetailPage}>
              <div className={styles.card_text}>{cutText(policy.name, 24)}</div>
            </div>
          ))}
        </div>
      </div>
      <Pagination
        activePage={currentPage} // 현재 선택된 페이지 번호를 나타냄
        itemsCountPerPage={itemsPerPage} // 한 페이지당 보여줄 아이템 개수
        totalItemsCount={totalItems} // 전체 아이템 개수 (페이지네이션 계산에 사용됨)
        pageRangeDisplayed={5} // 페이지 버튼 그룹에 보여줄 페이지 범위 개수
        onChange={handlePageChange} // 페이지 번호가 변경되었을 때 호출될 콜백 함수
        itemClass="page-item" // 각 페이지 번호 버튼을 감싸는 클래스
        linkClass="page-link" // 페이지 번호 버튼의 링크를 감싸는 클래스
      />

      <Footer />
    </div>
  );
}

export default SearchResults;
