import React, { useState, useEffect, useRef } from "react";
import styles from "./Dropdown.module.css";

function Dropdown() {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState("지역을 선택해 주세요");
  const dropdownRef = useRef(null);

  const options = ["전체", "서울", "경기", "부산", "대구", "인천"]; // 드롭다운 박스의 옵션들을 배열로 정의

  // 드롭다운 버튼을 클릭했을 때 호출되는 함수
  const handleDropdownClick = () => {
    setIsOpen(!isOpen); // isOpen 상태를 토글하여 드롭다운 내용을 보이거나 숨김
  };

  // 드롭다운 내용의 각 옵션을 클릭했을 때 호출되는 함수
  const handleOptionClick = (option) => {
    setIsOpen(false); // 드롭다운 내용을 숨김
    setSelectedOption(option); // 선택된 옵션을 selectedOption 상태로 설정
  };

  const handleOutsideClick = (event) => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      setIsOpen(false);
    }
  };

  return (
    <div
      className={styles.dropdown_container}
      ref={dropdownRef}
      onClick={handleOutsideClick}
    >
      <button
        className={`${styles.drop_btn} ${isOpen ? styles.opened : ""}`}
        onClick={handleDropdownClick}
      >
        {selectedOption}
      </button>
      {isOpen && (
        <div className={styles.dropdown_content}>
          {options.map((a, i) => (
            <div
              key={i}
              className={styles.option}
              onClick={() => handleOptionClick(a)}
            >
              {a}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Dropdown;
